import React from 'react';
const firebase = require('../firebase.js');
const db = firebase.db;
const st = firebase.storage;

class ImgRecognition extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            image: '',
            date: '',
            time: '',
            count: 0,
        }
        this.watchData = this.watchData.bind(this);
    }

    watchData(){
        let query = db.collection('PersonsCount').orderBy('time','desc').limit(1);

        let observer = query.onSnapshot(querySnapshot => {
            console.log(`Received query snapshot of size ${querySnapshot.size}`);

            querySnapshot.forEach(doc => {
                console.log(doc.id, '=>', doc.data());
              });

        }, err => {
            console.log(`Encountered error: ${err}`);
        });
    }

      componentDidMount(){
        this.watchData();
    }

  render(){
    return (
        <div>
            <div className="container">
                <h1>
                Image Recognition:
                </h1>
            </div>

            <div className="container">
                <p>
                Picture taken at this date: {this.state.date} at this time: {this.state.time} 
                </p>

                <p>
                Number of persons in this picture: {this.state.count}
                </p>
            </div>
        </div>
    );
  }

}

export default ImgRecognition;
