class ImgRecognition extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            count: 0,
            time: '',
            date: '',
            image
        }
        this.watchData = this.watchData.bind(this);
    }

   watchData(){
        let me = this;
        let query = db.collection('PersonsCount').orderBy('timestamp', 'desc').limit(1);
        let observer = query.onSnapshot(querySnapshot => {
            console.log(`Received query snapshot of size ${querySnapshot.size}`);

            let data = querySnapshot.docs[0].data();
            console.log(data)

            me.setState({
                count: data.count,
                time: data.timestamp.toDate().toLocaleTimeString("en-US"),
                date: data.timestamp.toDate().toLocaleDateString("en-US")
            });

        }, err => {
            console.log(`Encountered error: ${err}`);
        });
    }


      componentDidMount(){
        this.watchData();
    }

  render(){
    return (
        <div>
            <div className="container">
                <h1>
                Image Recognition:
                </h1>
                <h2>
                    There are {this.state.count} persons in this picture.
                </h2>
                <h3>
                   Date: {this.state.date}
                </h3>
                <h3>
                   Time: {this.state.time}
                </h3>
            </div>


        </div>
    );
  }

}

export default ImgRecognition;

