import React from 'react';
const firebase = require('../firebase.js');
const db = firebase.db;
const storage = firebase.storage;

class ImgRecognition extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            image: '',
            date: '',
            time: '',
            count: 0,
        }
        this.watchData = this.watchData.bind(this);
    }

    watchData(){
        var me = this;
        var imgCollection = db.collection('PersonsCount').orderBy('time','desc').limit(1);
        var observer = imgCollection.onSnapshot(snap => {
          let data = snap.docs[0].data();
          console.log(data)
          if(data.filename){
          console.log(`Received query snapshot`);
            storage.refFromURL('gs://smiles-ai-images').child(data.filename).getDownloadURL()
              .then(function(url) {
                console.log(url);
                let count = data.count
                let date = data.timestamp.toLocaleDateString("en-US")
                let time = data.timestamp.toLocaleTimeString("en-US");
                me.setState({
                  image:url,
                  date:date,
                  time:time,
                  count:count
                });
              });
          } 
        }, err => {
          console.log(`Encountered error: ${err}`);
        });
      };

      componentDidMount(){
        this.watchData();
    }

  render(){
    return (
        <div>
            <div className="container">
                <h1>
                Image Recognition:
                </h1>
            </div>

            <div className="container">
                <p>
                Picture taken at this date: {this.state.date} at this time: {this.state.time} 
                </p>

                <p>
                Number of persons in this picture: {this.state.count}
                </p>
            </div>
        </div>
    );
  }

}

export default ImgRecognition;
